from flask import Flask, requestfrom flask_cors import CORS, cross_originimport jsonimport sqlite3import pandas as pdimport numpy as npfrom datetime import datetime, datefrom urllib.parse import quoteimport requestsapp = Flask(__name__)CORS(app)def get_connection(db_path='candy.db'):    return sqlite3.connect(db_path)@app.route('/checkemail', methods=["POST"])def if_user_exists():    email = request.form["email"]    conn = get_connection()    c = conn.cursor()    c.execute("SELECT * FROM user WHERE email=(?)", [email])    users = c.fetchall()    conn.close()    if len(users) == 0:        return json.dumps("success")    else:        return json.dumps("exists")@app.route('/adduser', methods=["POST"])def add_user():    request_data = request.get_json()    username = request_data["username"]    password = request_data["password"]    gender = request_data["gender"]    major = request_data["major"]    email = request_data["email"]    pref1 = request_data["pref1"]    pref2 = request_data["pref2"]    pref3 = request_data["pref3"]    notes = request_data["notes"]    conn = get_connection()    c = conn.cursor()    c.execute("INSERT INTO user (firstName, password, gender, email, major, pref_1, pref_2, pref_3, summary) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?)",            [username, password, gender, email, major, pref1, pref2, pref3, notes])    conn.commit()    conn.close()    response_text = {}    response_text["message"] = "Success"    return json.dumps(response_text)@app.route('/getuser', methods=["POST"])def get_user():    request_data = request.get_json()    email = request_data["email"]    conn = get_connection()    c = conn.cursor()    c.execute("SELECT firstName, password, gender, email, major, pref_1, pref_2, pref_3, summary FROM user WHERE email=(?)", [email])    users = c.fetchall()    conn.close()    if len(users) == 0:        return json.dumps({"message": "Email doesn't exists."})    else:        user = user